import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    // description: "Finest fish and veggies",
    description: "Finest fish (like omri) and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    // description: "A german specialty!",
    description: "An excellent schnitzel that always makes Shahar happy ",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    // description: "American, raw, meaty",
    description: "Handmade by our hamburger expert Noam Shamir",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m5",
    name: "Spaghetti Bolognese",
    description: "Italian spaghetti with a meaty, chilli sauce",
    price: 13.99,
  },
];

function AvailableMeals() {
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
}

export default AvailableMeals;
